    private void GenerateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_GenerateActionPerformed
        DefaultTableModel model = (DefaultTableModel) listMakanan.getModel();
        main.knapsack(caloryNeed);  
        if(model.getRowCount() == 0) {
            addTable(model);
           
            Float totalCal1 = 0f;
            Float totalCal2 = 0f;

            for (int i = 0; i < main.all1.size(); i++){
                totalCal1 += main.all1.get(i).calories;
            }
            for (int i = 0; i < main.all2.size(); i++){
                totalCal2 += main.all2.get(i).calories;
            }
            totalCal1 += totalCal2;
            calMeal.setText(totalCal1.toString());
            calMeal.setVisible(true);
        }
    }//GEN-LAST:event_GenerateActionPerformed

