    public void addTable(DefaultTableModel model){
        Object rowData[] = new Object[6];
        for (int i = 0; i < main.all1.size(); i++){
            String a = String.valueOf(main.all1.get(i).calories) + " kcals";
            rowData[0] = main.all1.get(i).name;
            rowData[1] = a;
            rowData[2] = fullText(main.all1.get(i).carbs);
            rowData[3] = fullText(main.all1.get(i).fat);
            rowData[4] = fullText(main.all1.get(i).protein);
            rowData[5] = fullText(main.all1.get(i).fibre);
            model.addRow(rowData);
        }
        
        for (int i = 0; i < main.all2.size(); i++){
            String a = String.valueOf(main.all2.get(i).calories) + " kcals";
            rowData[0] = main.all2.get(i).name;
            rowData[1] = a;
            rowData[2] = fullText(main.all2.get(i).carbs);
            rowData[3] = fullText(main.all2.get(i).fat);
            rowData[4] = fullText(main.all2.get(i).protein);
            rowData[5] = fullText(main.all2.get(i).fibre);
            model.addRow(rowData);
        }
    }
    
    public String fullText(Float x) {
        String s = String.valueOf(x) + " g";
        return s;
    }

